import React, { Component } from "react";
import "./Interests.css";

export class Interests extends Component {
  constructor() {
    super();
    this.state = {
      interests: [], // Initially empty
      newInterest: ""
    };
  }

  componentDidMount() {
    // Fetch existing interests from local storage or database
    const savedInterests = JSON.parse(localStorage.getItem("userInterests")) || [];
    this.setState({ interests: savedInterests });
  }

  handleAddInterest = () => {
    const { interests, newInterest } = this.state;
    if (newInterest && !interests.includes(newInterest)) {
      const updatedInterests = [...interests, newInterest];
      this.setState({ interests: updatedInterests, newInterest: "" });
      localStorage.setItem("userInterests", JSON.stringify(updatedInterests));
    }
  };

  handleInputChange = (e) => {
    this.setState({ newInterest: e.target.value });
  };

  render() {
    const { interests, newInterest } = this.state;

    return (
      <div className="interests-container">
        <h2>Your News Interests</h2>
        <div className="interests-list">
          {interests.length === 0 ? (
            <p>Please select your news interests.</p>
          ) : (
            <ul>
              {interests.map((interest, index) => (
                <li key={index}>{interest}</li>
              ))}
            </ul>
          )}
        </div>
        <div className="add-interest">
          <input
            type="text"
            value={newInterest}
            onChange={this.handleInputChange}
            placeholder="Add a new interest"
          />
          <button onClick={this.handleAddInterest} className="add-button">Add</button>
        </div>
      </div>
    );
  }
}

export default Interests;
