// frontend/src/components/Interests.js
import React, { Component } from "react";
import News from "./News"; // Import News component to display news by interest
import axios from "axios";
import "./Interests.css";

export class Interests extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCategory: "",
      categories: ["business", "entertainment", "health", "science", "sports", "technology"],
      interests: [],
      showNews: false, // Track if user clicked "View News"
    };
  }

  componentDidMount() {
    this.loadInterests();
  }

  loadInterests = async () => {
    try {
      const response = await axios.get("http://localhost:5000/user/interests", {
        params: { email: this.props.email },
      });
      if (response.data.success) {
        this.setState({ interests: response.data.interests });
      }
    } catch (error) {
      console.error("Error loading interests:", error);
    }
  };

  handleCategoryChange = (e) => {
    this.setState({ selectedCategory: e.target.value });
  };

  addInterest = async () => {
    const { selectedCategory, interests } = this.state;
    if (selectedCategory && !interests.includes(selectedCategory)) {
      const updatedInterests = [...interests, selectedCategory];
      this.setState({ interests: updatedInterests, selectedCategory: "" });

      try {
        await axios.post("http://localhost:5000/user/interests", {
          email: this.props.email,
          interests: updatedInterests,
        });
      } catch (error) {
        console.error("Error saving interests:", error);
      }
    }
  };

  removeInterest = async (interestToRemove) => {
    const updatedInterests = this.state.interests.filter(
      (interest) => interest !== interestToRemove
    );
    this.setState({ interests: updatedInterests });

    try {
      await axios.post("http://localhost:5000/user/remove-interest", {
        email: this.props.email,
        interest: interestToRemove,
      });
    } catch (error) {
      console.error("Error removing interest:", error);
    }
  };

  // Trigger News component to display based on selected interest
  viewNews = () => {
    this.setState({ showNews: true });
  };

  render() {
    const { selectedCategory, categories, interests, showNews } = this.state;
    const { api_key } = this.props; // Pass API key from parent component

    return (
      <div className="interests-container">
        <h2>Your News Interests</h2>
        <div className="category-selection">
          <label htmlFor="categories">Select your news category:</label>
          <select
            id="categories"
            value={selectedCategory}
            onChange={this.handleCategoryChange}
          >
            <option value="">-- Select a category --</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
          <button onClick={this.addInterest} className="add-button">Add</button>
        </div>

        <div className="interests-list">
          {interests.map((interest, index) => (
            <div key={index} className="interest-box">
              {interest.charAt(0).toUpperCase() + interest.slice(1)}
              <button onClick={() => this.removeInterest(interest)} className="remove-button">
                Remove
              </button>
            </div>
          ))}
        </div>

        {interests.length > 0 && (
          <button onClick={this.viewNews} className="view-news-button">
            View News
          </button>
        )}

        {/* Render News component if "View News" was clicked */}
        {showNews && selectedCategory && (
          <News category={selectedCategory} api_key={api_key} />
        )}
      </div>
    );
  }
}

export default Interests;
