// frontend/src/components/Navbar.js
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ isLoggedIn, email, onLogout, onCountryChange }) => {
  const navigate = useNavigate();
  const [selectedCountry, setSelectedCountry] = useState("");

  const handleLogout = () => {
    onLogout();
    navigate("/login");
  };

  const handleCountrySelect = (event) => {
    const country = event.target.value;
    setSelectedCountry(country);
    onCountryChange(country); // Update the selected country in the parent component
  };

  const countries = [
    { code: "us", name: "United States" },
    { code: "in", name: "India" },
    { code: "gb", name: "United Kingdom" },
    { code: "ca", name: "Canada" },
    { code: "au", name: "Australia" },
    { code: "fr", name: "France" },
    { code: "de", name: "Germany" },
    // Add more countries as needed
  ];

  return (
    <nav className="navbar navbar-expand-lg bg-body-tertiary">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          News Network
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className="nav-link active" aria-current="page" to="/">
                General
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link active" aria-current="page" to="/business">
                Business
              </Link>
            </li>
            {/* Other categories */}

            {/* Country dropdown */}
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="countryDropdown"
                role="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                Country
              </a>
              <ul className="dropdown-menu" aria-labelledby="countryDropdown">
                {countries.map((country) => (
                  <li key={country.code}>
                    <button
                      className="dropdown-item"
                      value={country.code}
                      onClick={handleCountrySelect}
                    >
                      {country.name}
                    </button>
                  </li>
                ))}
              </ul>
            </li>
            {isLoggedIn ? (
              <>
                {/* Additional authenticated user items */}
                <li className="nav-item dropdown">
                  <a
                    className="nav-link dropdown-toggle"
                    href="#"
                    id="navbarDropdown"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    {email}
                  </a>
                  <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="navbarDropdown">
                    <li>
                      <button className="dropdown-item" onClick={handleLogout}>
                        Logout
                      </button>
                    </li>
                  </ul>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <Link className="nav-link active" aria-current="page" to="/login">
                  Login
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
