import React, { Component } from "react";
import "./Interests.css";
import { Link } from "react-router-dom";

export class Interests extends Component {
  constructor() {
    super();
    this.state = {
      interests: [],
      newInterest: "",
      selectedCategory: "", // Track the selected news category
      categories: [
        "business",
        "entertainment",
        "health",
        "science",
        "sports",
        "technology",
      ], // Available categories
    };
  }

  componentDidMount() {
    const savedInterests = JSON.parse(localStorage.getItem("userInterests")) || [];
    this.setState({ interests: savedInterests });
  }

  handleAddInterest = () => {
    const { interests, newInterest } = this.state;
    if (newInterest && !interests.includes(newInterest)) {
      const updatedInterests = [...interests, newInterest];
      this.setState({ interests: updatedInterests, newInterest: "" });
      localStorage.setItem("userInterests", JSON.stringify(updatedInterests));
    }
  };

  handleInputChange = (e) => {
    this.setState({ newInterest: e.target.value });
  };

  handleCategoryChange = (e) => {
    this.setState({ selectedCategory: e.target.value });
  };

  render() {
    const { interests, newInterest, selectedCategory, categories } = this.state;

    return (
      <div className="interests-container">
        <h2>Your News Interests</h2>
        <div className="interests-list">
          {interests.length === 0 ? (
            <p>Please select your news interests.</p>
          ) : (
            <ul>
              {interests.map((interest, index) => (
                <li key={index}>{interest}</li>
              ))}
            </ul>
          )}
        </div>

        <div className="add-interest">
          <input
            type="text"
            value={newInterest}
            onChange={this.handleInputChange}
            placeholder="Add a new interest"
          />
          <button onClick={this.handleAddInterest} className="add-button">Add</button>
        </div>

        <div className="category-selection">
          <label htmlFor="categories">Select your news category:</label>
          <select
            id="categories"
            value={selectedCategory}
            onChange={this.handleCategoryChange}
          >
            <option value="">-- Select a category --</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
          {selectedCategory && (
            <Link
              to={`/${selectedCategory}`} // Link to the selected category's route
              className="view-news-button"
            >
              View News
            </Link>
          )}
        </div>
      </div>
    );
  }
}

export default Interests;
