import React, { Component } from "react";

export class NewsUpdate extends Component {
  render() {
    let { title, imgUrl, newsUrl, author, date } = this.props;
    return (
      <div className="card" style={{ width: "25rem" }}>
        <div className="card-header">Author : {author}</div>
        <img src={imgUrl} className="card-img-top" alt="..." height="200px" />
        <div className="card-body">
          <div style={{ height: "100px" }}>
            <h5 className="card-title">{title}</h5>
          </div>
          <a href={newsUrl} className="btn btn-primary">
            Read
          </a>
        </div>
        <div className="card-footer text-muted">{date}</div>
      </div>
    );
  }
}

export default NewsUpdate;
