import React, { Component } from "react";
import NewsUpdate from "./NewsUpdate";
import Spinner from "./Spinner";
import PropTypes from "prop-types";

export class News extends Component {
  static defaultProps = {
    category: "General",
  };

  static propTypes = {
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }

  async updateNews() {
    const url = `https://newsapi.org/v2/top-headlines?&language=en&category=${this.props.category}&apiKey=585f3a3a24764a82a970c0f33e9b28ba&page=${this.state.page}&pageSize=12`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page,
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }

  async componentDidMount() {
    this.updateNews();
  }

  nextClick = async () => {
    this.setState({ page: this.state.page + 1 });
    this.updateNews();
  };

  previousClick = async () => {
    this.setState({ page: this.state.page - 1 });
    this.updateNews();
  };

  render() {
    return (
      <div className="container my-3">
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.previousClick}
          >
            Previous
          </button>
          <button
            type="button"
            className="btn btn-dark"
            onClick={this.nextClick}
          >
            Next
          </button>
        </div>
        <h2 className="text-center">TOP HEADLINES</h2>
        {this.state.loading && <Spinner />}
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsUpdate
                  title={element.title}
                  newsUrl={element.url}
                  description={element.description.slice(0, 100)}
                  imgUrl={element.urlToImage}
                  source={element.source.name}
                  author={element.author}
                />
              </div>
            );
          })}
        </div>
        {this.state.loading && <Spinner />}
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.previousClick}
          >
            Previous
          </button>
          <button
            type="button"
            className="btn btn-dark"
            onClick={this.nextClick}
          >
            Next
          </button>
        </div>
      </div>
    );
  }
}

export default News;
