import React, { Component } from "react";
import NewsUpdate from "./NewsUpdate";
import Spinner from "./Spinner";
import PropTypes from "prop-types";

export class News extends Component {
  static defaultProps = {
    category: "general", // Changed to lowercase for consistency
  };

  static propTypes = {
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true, // Initialize loading state
      page: 1,
      totalResults: 0,
    };
  }

  async updateNews() {
    this.setState({ loading: true }); // Set loading to true before API call
    const url = `https://newsapi.org/v2/top-headlines?&language=en&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page}&pageSize=12`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false, // Set loading to false after data is fetched
    });
  }

  async componentDidMount() {
    this.updateNews();
  }

  nextClick = async () => {
    this.setState({ page: this.state.page + 1 }, this.updateNews);
  };

  previousClick = async () => {
    this.setState({ page: this.state.page - 1 }, this.updateNews);
  };

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">TOP HEADLINES</h2>
        {this.state.loading && <Spinner />}
        <div className="row">
          {!this.state.loading &&
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsUpdate
                    title={element.title}
                    newsUrl={element.url}
                    description={
                      element.description
                        ? element.description.slice(0, 100)
                        : ""
                    }
                    imgUrl={element.urlToImage}
                    source={element.source.name}
                    author={element.author}
                  />
                </div>
              );
            })}
        </div>
        {/*{this.state.loading && <Spinner />}*/}
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.previousClick}
          >
            Previous
          </button>
          <h5>Total Results : {this.state.totalResults}</h5>
          <button
            disabled={
              this.state.page + 1 > Math.ceil(this.state.totalResults / 12)
            }
            type="button"
            className="btn btn-dark"
            onClick={this.nextClick}
          >
            Next
          </button>
        </div>
      </div>
    );
  }
}

export default News;
