// frontend/src/components/News.js

import React, { Component } from "react";
import NewsUpdate from "./NewsUpdate";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import { checkIfFakeNews } from "../services/api"; // Import fake news detection API function
import "./News.css";

export class News extends Component {
  static defaultProps = {
    category: "general",
  };

  static propTypes = {
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  // Function to detect fake news for each article and update state
  async detectFakeNews(articles) {
    const updatedArticles = await Promise.all(
      articles.map(async (article) => {
        const result = await checkIfFakeNews(article.description || ""); // Send description to API for fake news check
        return { ...article, isFake: result.is_fake };
      })
    );
    return updatedArticles;
  }

  // Function to fetch news and update state
  async updateNews() {
    this.setState({ loading: true });
    const url = `https://newsapi.org/v2/top-headlines?&language=en&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page}&pageSize=12`;

    let data = await fetch(url);
    let parsedData = await data.json();

    const articlesWithFakeStatus = await this.detectFakeNews(parsedData.articles);

    this.setState({
      articles: articlesWithFakeStatus,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  async componentDidMount() {
    this.updateNews();
  }

  nextClick = async () => {
    this.setState({ page: this.state.page + 1 }, this.updateNews);
  };

  previousClick = async () => {
    this.setState({ page: this.state.page - 1 }, this.updateNews);
  };

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">TOP HEADLINES</h2>
        {this.state.loading && <Spinner />}

        <div className="row">
          {!this.state.loading &&
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsUpdate
                    title={element.title}
                    newsUrl={element.url}
                    description={
                      element.description ? element.description.slice(0, 100) : ""
                    }
                    imgUrl={element.urlToImage}
                    source={element.source.name}
                    author={element.author}
                    isFake={element.isFake} // Pass isFake status to NewsUpdate
                  />
                </div>
              );
            })}
        </div>

        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.previousClick}
          >
            Previous
          </button>
          <h5>Total Results : {this.state.totalResults}</h5>
          <button
            disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 12)}
            type="button"
            className="btn btn-dark"
            onClick={this.nextClick}
          >
            Next
          </button>
        </div>
      </div>
    );
  }
}

export default News;
