// frontend/src/App.js
import React, { Component } from "react";
import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import News from "./components/News";
import NewsChecker from "./components/NewsChecker";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Interests from "./components/Interests";

export default class App extends Component {
  api_key = "585f3a3a24764a82a970c0f33e9b28ba";

  state = {
    isLoggedIn: false,
    email: null,
    country: "us", // Default country
  };

  handleLogin = (userEmail) => {
    this.setState({ isLoggedIn: true, email: userEmail });
  };

  handleLogout = () => {
    this.setState({ isLoggedIn: false, email: null });
  };

  handleCountryChange = (country) => {
    this.setState({ country }); // Update country state when a new country is selected
  };

  render() {
    const { isLoggedIn, email, country } = this.state;

    return (
      <div>
        <Navbar
          isLoggedIn={isLoggedIn}
          email={email}
          onLogout={this.handleLogout}
          onCountryChange={this.handleCountryChange} // Pass country change handler
        />
        <Routes>
          <Route
            path="/"
            element={<News key="general" api_key={this.api_key} category="general" country={country} />}
          />
          <Route
            path="/business"
            element={<News key="business" api_key={this.api_key} category="business" country={country} />}
          />
          <Route
            path="/entertainment"
            element={<News key="entertainment" api_key={this.api_key} category="entertainment" country={country} />}
          />
          <Route
            path="/health"
            element={<News key="health" api_key={this.api_key} category="health" country={country} />}
          />
          <Route
            path="/science"
            element={<News key="science" api_key={this.api_key} category="science" country={country} />}
          />
          <Route
            path="/sports"
            element={<News key="sports" api_key={this.api_key} category="sports" country={country} />}
          />
          <Route
            path="/technology"
            element={<News key="technology" api_key={this.api_key} category="technology" country={country} />}
          />
          <Route path="/check-fake-news" element={<NewsChecker />} />
          <Route path="/login" element={<Login onLogin={this.handleLogin} />} />
          <Route path="/signup" element={<Signup />} />
          <Route
            path="/interests"
            element={isLoggedIn ? <Interests email={email} /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    );
  }
}
