// frontend/src/components/Login.js

import React, { Component } from "react";
import "./Login.css";

export class Login extends Component {
  render() {
    return (
      <div className="login-container">
        <div className="login-box">
          <h2>Welcome to our News Aggregator</h2>
          <form>
            <div className="form-group">
              <label htmlFor="email">Email address</label>
              <input type="email" id="email" className="form-control" placeholder="Enter your email" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="password" id="password" className="form-control" placeholder="Enter your password" />
            </div>
            <div className="form-group">
              <label htmlFor="confirm-password">Confirm Password</label>
              <input type="password" id="confirm-password" className="form-control" placeholder="Confirm your password" />
            </div>
            <button type="submit" className="submit-button">Submit</button>
          </form>
          <p className="register-prompt">
            Don't have an account? <button className="register-button">Register</button>
          </p>
        </div>
      </div>
    );
  }
}

export default Login;
