import React, { Component } from "react";
import axios from "axios";
import "./Signup.css";

export class Signup extends Component {
  state = {
    username: "", // Change "email" to "username"
    password: "",
    confirmPassword: "",
    message: ""
  };

  handleInputChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  handleRegister = async (e) => {
    e.preventDefault();
    const { username, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      this.setState({ message: "Passwords do not match." });
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/signup", {
        username, // Use "username" instead of "email"
        password,
      });
      this.setState({ message: response.data.message });
      // Redirect to login page after successful registration
      window.location.href = "/Login";
    } catch (error) {
      this.setState({ message: error.response?.data?.message || "Registration failed." });
    }
  };

  render() {
    return (
      <div className="signup-container">
        <div className="signup-box">
          <h2>Register to our News Aggregator Website</h2>
          <form onSubmit={this.handleRegister}>
            <div className="form-group">
              <label htmlFor="username">Email address</label>
              <input
                type="email"
                id="username" // Change "email" to "username"
                className="form-control"
                placeholder="Enter your email"
                value={this.state.username}
                onChange={this.handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                className="form-control"
                placeholder="Enter your password"
                value={this.state.password}
                onChange={this.handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                type="password"
                id="confirmPassword"
                className="form-control"
                placeholder="Confirm your password"
                value={this.state.confirmPassword}
                onChange={this.handleInputChange}
              />
            </div>
            <button type="submit" className="register-button">Register</button>
          </form>
          <p className="message">{this.state.message}</p>
        </div>
      </div>
    );
  }
}

export default Signup;
