// frontend/src/components/Login.js

import React, { Component } from "react";
import "./Login.css";
import axios from "axios"; // Import axios to make HTTP requests

export class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      errorMessage: ""
    };
  }

  handleInputChange = (event) => {
    this.setState({ [event.target.id]: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { email, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      this.setState({ errorMessage: "Passwords do not match!" });
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/login", {
        email,
        password
      });

      if (response.data.success) {
        alert("Login successful");
        // Redirect or take other actions upon successful login
      } else {
        this.setState({ errorMessage: response.data.message });
      }
    } catch (error) {
      this.setState({ errorMessage: "An error occurred. Please try again." });
    }
  };

  render() {
    const { email, password, confirmPassword, errorMessage } = this.state;

    return (
      <div className="login-container">
        <div className="login-box">
          <h2>Welcome to our News Aggregator</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email address</label>
              <input
                type="email"
                id="email"
                className="form-control"
                placeholder="Enter your email"
                value={email}
                onChange={this.handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                className="form-control"
                placeholder="Enter your password"
                value={password}
                onChange={this.handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="confirm-password">Confirm Password</label>
              <input
                type="password"
                id="confirmPassword"
                className="form-control"
                placeholder="Confirm your password"
                value={confirmPassword}
                onChange={this.handleInputChange}
              />
            </div>
            <button type="submit" className="submit-button">Submit</button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
          </form>
          <p className="register-prompt">
  Don't have an account? <button className="register-button" onClick={() => window.location.href = "/signup"}>Register</button>
</p>
        </div>
      </div>
    );
  }
}

export default Login;
