// frontend/src/components/News.js
import React, { Component } from "react";
import NewsUpdate from "./NewsUpdate";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import { checkIfFakeNews } from "../services/api";
import "./News.css";

export class News extends Component {
  static defaultProps = {
    category: "general",
    country: "us",
  };

  static propTypes = {
    category: PropTypes.string,
    api_key: PropTypes.string.isRequired,
    country: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async updateNews() {
    this.setState({ loading: true });
    const { category, api_key, country } = this.props;
    const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=${api_key}&page=${this.state.page}&pageSize=12`;

    let data = await fetch(url);
    let parsedData = await data.json();

    const articlesWithFakeStatus = await this.detectFakeNews(parsedData.articles);

    this.setState({
      articles: articlesWithFakeStatus,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  componentDidMount() {
    this.updateNews();
  }

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">TOP HEADLINES</h2>
        {this.state.loading && <Spinner />}
        <div className="row">
          {!this.state.loading &&
            this.state.articles.map((article) => (
              <div className="col-md-4" key={article.url}>
                <NewsUpdate
                  title={article.title}
                  description={article.description}
                  imgUrl={article.urlToImage}
                  newsUrl={article.url}
                  source={article.source.name}
                  author={article.author}
                />
              </div>
            ))}
        </div>
      </div>
    );
  }
}

export default News;
