// frontend/src/components/Login.js

import React, { Component } from "react";
import "./Login.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login({ onLogin }) {
  const navigate = useNavigate();

  const [formState, setFormState] = React.useState({
    email: "",
    password: "",
    confirmPassword: "",
    errorMessage: ""
  });

  const handleInputChange = (event) => {
    setFormState({ ...formState, [event.target.id]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { email, password, confirmPassword } = formState;

    if (password !== confirmPassword) {
      setFormState({ ...formState, errorMessage: "Passwords do not match!" });
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/login", {
        email,
        password
      });

      if (response.data.success) {
        alert("Login successful");
        onLogin(); // Update the login state in App.js
        navigate("/interests"); // Redirect to Interests page
      } else {
        setFormState({ ...formState, errorMessage: response.data.message });
      }
    } catch (error) {
      setFormState({
        ...formState,
        errorMessage: "An error occurred. Please try again."
      });
    }
  };

  const { email, password, confirmPassword, errorMessage } = formState;

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Welcome to our News Aggregator</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email address</label>
            <input
              type="email"
              id="email"
              className="form-control"
              placeholder="Enter your email"
              value={email}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              className="form-control"
              placeholder="Enter your password"
              value={password}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="confirm-password">Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              className="form-control"
              placeholder="Confirm your password"
              value={confirmPassword}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit" className="submit-button">Submit</button>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </form>
        <p className="register-prompt">
          Don't have an account? <button className="register-button" onClick={() => navigate("/signup")}>Register</button>
        </p>
      </div>
    </div>
  );
}
