// frontend/src/App.js
import React, { Component } from "react";
import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import News from "./components/News";
import NewsChecker from "./components/NewsChecker";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Interests from "./components/Interests";

export default class App extends Component {
  api_key = "585f3a3a24764a82a970c0f33e9b28ba";

  state = {
    isLoggedIn: false,
    email: null,
  };

  handleLogin = (userEmail) => {
    this.setState({ isLoggedIn: true, email: userEmail });
  };

  handleLogout = () => {
    this.setState({ isLoggedIn: false, email: null });
  };

  render() {
    return (
      <div>
        <Navbar
          isLoggedIn={this.state.isLoggedIn}
          email={this.state.email}
          onLogout={this.handleLogout}
        />
        <Routes>
          <Route
            path="/"
            element={<News key="general" api_key={this.api_key} category="general" />}
          />
          <Route path="/business" element={<News key="business" api_key={this.api_key} category="business" />} />
          <Route path="/entertainment" element={<News key="entertainment" api_key={this.api_key} category="entertainment" />} />
          <Route path="/health" element={<News key="health" api_key={this.api_key} category="health" />} />
          <Route path="/science" element={<News key="science" api_key={this.api_key} category="science" />} />
          <Route path="/sports" element={<News key="sports" api_key={this.api_key} category="sports" />} />
          <Route path="/technology" element={<News key="technology" api_key={this.api_key} category="technology" />} />
          <Route path="/check-fake-news" element={<NewsChecker />} />
          <Route path="/login" element={<Login onLogin={this.handleLogin} />} />
          <Route path="/signup" element={<Signup />} />
          <Route
            path="/interests"
            element={this.state.isLoggedIn ? <Interests email={this.state.email} /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    );
  }
}
