import React, { Component } from 'react';

class Interests extends Component {
  state = {
    selectedCategories: [],
    newsArticles: [],
    newInterest: '',
  };

  // Function to fetch news based on selected categories
  fetchNews = () => {
    const { selectedCategories } = this.state;
    const fetchPromises = selectedCategories.map((category) =>
      fetch(`https://newsapi.org/v2/top-headlines?category=${category}&apiKey=YOUR_API_KEY`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => ({ category, articles: data.articles || [] })) // Fallback to empty array
        .catch((error) => {
          console.error('Error fetching news:', error);
          return { category, articles: [] }; // Return empty array on error
        })
    );

    Promise.all(fetchPromises)
      .then((results) => {
        const newsArticles = results.flatMap((result) =>
          result.articles ? result.articles.map((article) => ({
            category: result.category,
            ...article,
          })) : []
        );
        this.setState({ newsArticles });
      });
  };

  // Function to handle category selection
  handleCategoryChange = (e) => {
    const { selectedCategories } = this.state;
    const category = e.target.value;

    if (category && !selectedCategories.includes(category)) {
      this.setState((prevState) => ({
        selectedCategories: [...prevState.selectedCategories, category],
      }), () => {
        console.log('Selected categories:', this.state.selectedCategories); // Log selected categories
        this.fetchNews();
      });
    }
  };

  // Function to add a new interest
  handleNewInterestChange = (e) => {
    this.setState({ newInterest: e.target.value });
  };

  // Function to handle adding a new interest
  addNewInterest = () => {
    const { newInterest, selectedCategories } = this.state;
    if (newInterest && !selectedCategories.includes(newInterest)) {
      this.setState((prevState) => ({
        selectedCategories: [...prevState.selectedCategories, newInterest],
        newInterest: '', // Clear the input field after adding
      }), this.fetchNews);
    }
  };

  render() {
    const { selectedCategories, newsArticles, newInterest } = this.state;

    return (
      <div>
        <h2>Your News Interests</h2>
        <div>
          {selectedCategories.map((category, index) => (
            <div key={index}>{category}</div>
          ))}
        </div>
        <input
          type="text"
          value={newInterest}
          onChange={this.handleNewInterestChange}
          placeholder="Add a new interest"
        />
        <button onClick={this.addNewInterest}>Add</button>
        
        <select onChange={this.handleCategoryChange}>
          <option value="">Select a news category</option>
          <option value="general">General</option>
          <option value="business">Business</option>
          <option value="health">Health</option>
          <option value="science">Science</option>
          <option value="sports">Sports</option>
          <option value="technology">Technology</option>
          <option value="entertainment">Entertainment</option>
        </select>

        <h3>Fetched News Articles</h3>
        <div>
          {newsArticles.length > 0 ? (
            newsArticles.map((article, index) => (
              <div key={index}>
                <h4>{article.title}</h4>
                <p>{article.description}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
              </div>
            ))
          ) : (
            <p>No articles found.</p>
          )}
        </div>
      </div>
    );
  }
}

export default Interests;
