import React, { Component } from "react";
import "./Interests.css";
import { Link } from "react-router-dom";

export class Interests extends Component {
  constructor() {
    super();
    this.state = {
      selectedCategory: "", // Track the selected news category
      categories: [
        "business",
        "entertainment",
        "health",
        "science",
        "sports",
        "technology",
      ], // Available categories
    };
  }

  handleCategoryChange = (e) => {
    this.setState({ selectedCategory: e.target.value });
  };

  render() {
    const { selectedCategory, categories } = this.state;

    return (
      <div className="interests-container">
        <h2>Your News Interests</h2>

        <div className="category-selection">
          <label htmlFor="categories">Select your news category:</label>
          <select
            id="categories"
            value={selectedCategory}
            onChange={this.handleCategoryChange}
          >
            <option value="">-- Select a category --</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
          {selectedCategory && (
            <Link
              to={`/${selectedCategory}`} // Link to the selected category's route
              className="view-news-button"
            >
              View News
            </Link>
          )}
        </div>
      </div>
    );
  }
}

export default Interests;
